const payrollBatchMessage = workflowContext.actions.Parse_payroll_batch_message.outputs.body;
const payrollDetail = workflowContext.actions.Compose_Payroll_Detail_current_item.outputs;

const detailPayCode = payrollDetail["pics_programname"];

const payPeriodStart = new Date(payrollDetail["new_payrollperiodstartdate"] || "01-01-1900");
const payPeriodEnd = new Date(payrollDetail["new_payrollperiodenddate"] || "01-01-1900");

const I_vUPRTRXCD = detailPayCode === "Other Flat" || detailPayCode === "Other Hourly" ? "HOURLY" : detailPayCode;

let program;
switch (payrollDetail["pics_programname"]) {
    case "CFSS Budget":
        program = "CFSSB";
        break;
    case "Private Pay":
    case "Participant":
        program = "PP";
        break;
    case "Stearns County Personal Support":
        program = "SCPS";
        break;
    default:
        program = payrollDetail["pics_programname"];
        break;
}

  return {
    I_vBACHNUMB: payrollBatchMessage["contentData"]["document"]["new_batchid"],
    I_vEMPLOYID: payrollDetail["employee.pics_employeenumber"],
    I_vPAYRTAMT: payrollDetail["new_rateofpay"],
    I_vUPRTRXCD,
    I_vCOMPTRTP: 1,
    I_vSALCHG: 0,
    I_vHRLYPYRT: payrollDetail["new_rateofpay"],
    I_vSHFTCODE: program,
    I_vTRXBEGDT: `${payPeriodStart.getMonth() + 1}-${payPeriodStart.getDate()}-${payPeriodStart.getFullYear()}`,
    I_vTRXENDDT: `${payPeriodEnd.getMonth() + 1}-${payPeriodEnd.getDate()}-${payPeriodEnd.getFullYear()}`,
    I_vTRXHRUNT: payrollDetail["new_hours"],
    I_vVARDBAMT: 0,
    I_vVARDBPCT: 0,
    I_vRECEIPTS: 0,
    I_vDAYSWRDK: 0,
    I_vWKSWRKD: 0,
    I_vDEPRTMNT: "",
    I_vJOBTITLE: "",
    I_vSTATECD: "",
    I_vLOCALTAX: "",
    I_vSUTASTAT: "",
    I_vSHFTPREM: 0,
    I_vRequesterTrx: 0,
    I_vUSERID: "",
    I_vUSRDEFND1: "",
    I_vUSRDEFND2: "",
    I_vUSRDEFND3: "",
    I_vUSRDEFND4: "",
    I_vUSRDEFND5: "",
    I_vWRKRCOMP: 8842
  };

